import React, { useState, useEffect } from 'react';
import { CategoryList } from './components/CategoryList';
import { ProductList } from './components/ProductList';
import { Category, Product } from './types';
import { categoryService } from './api/categoryService';

// Временные демо-данные для продуктов (пока не подключим API)
const demoProducts: Product[] = [
  {
    id: 1,
    name: 'iPhone 13',
    description: 'Последняя модель iPhone с мощной камерой',
    price: 79990,
    image: 'https://via.placeholder.com/300',
    categoryId: 1,
  },
  {
    id: 2,
    name: 'Samsung Galaxy S21',
    description: 'Флагманский смартфон от Samsung',
    price: 69990,
    image: 'https://via.placeholder.com/300',
    categoryId: 1,
  },
  {
    id: 3,
    name: 'Джинсы Levi\'s',
    description: 'Классические джинсы прямого кроя',
    price: 5990,
    image: 'https://via.placeholder.com/300',
    categoryId: 2,
  },
];

function App() {
  const [categories, setCategories] = useState<Category[]>([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState<number | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadCategories();
  }, []);

  const loadCategories = async () => {
    try {
      const data = await categoryService.getCategories();
      setCategories(data);
      setError(null);
    } catch (err) {
      setError('Ошибка при загрузке категорий');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = selectedCategoryId
    ? demoProducts.filter((product) => product.categoryId === selectedCategoryId)
    : demoProducts;

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-xl text-gray-600">Загрузка...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-xl text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <h1 className="text-3xl font-bold text-gray-900">Каталог товаров</h1>
        </div>
      </header>
      <main className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="md:col-span-1">
            <CategoryList
              categories={categories}
              selectedCategoryId={selectedCategoryId}
              onSelectCategory={setSelectedCategoryId}
            />
          </div>
          <div className="md:col-span-3">
            <ProductList products={filteredProducts} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
