import React, { useState } from 'react';
import { CategoryList } from './components/CategoryList';
import { ProductList } from './components/ProductList';
import { Category, Product } from './types';

// Демо-данные для примера
const demoCategories: Category[] = [
  {
    id: 1,
    name: 'Электроника',
    description: 'Смартфоны, планшеты и другие гаджеты',
  },
  {
    id: 2,
    name: 'Одежда',
    description: 'Мужская и женская одежда',
  },
  {
    id: 3,
    name: 'Книги',
    description: 'Художественная литература и учебники',
  },
];

const demoProducts: Product[] = [
  {
    id: 1,
    name: 'iPhone 13',
    description: 'Последняя модель iPhone с мощной камерой',
    price: 79990,
    image: 'https://via.placeholder.com/300',
    categoryId: 1,
  },
  {
    id: 2,
    name: 'Samsung Galaxy S21',
    description: 'Флагманский смартфон от Samsung',
    price: 69990,
    image: 'https://via.placeholder.com/300',
    categoryId: 1,
  },
  {
    id: 3,
    name: 'Джинсы Levi\'s',
    description: 'Классические джинсы прямого кроя',
    price: 5990,
    image: 'https://via.placeholder.com/300',
    categoryId: 2,
  },
];

function App() {
  const [selectedCategoryId, setSelectedCategoryId] = useState<number | null>(null);

  const filteredProducts = selectedCategoryId
    ? demoProducts.filter((product) => product.categoryId === selectedCategoryId)
    : demoProducts;

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <h1 className="text-3xl font-bold text-gray-900">Каталог товаров</h1>
        </div>
      </header>
      <main className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="md:col-span-1">
            <CategoryList
              categories={demoCategories}
              selectedCategoryId={selectedCategoryId}
              onSelectCategory={setSelectedCategoryId}
            />
          </div>
          <div className="md:col-span-3">
            <ProductList products={filteredProducts} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
